import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Chat from '../components/Chat';
import {io} from 'socket.io-client';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCommentDots } from '@fortawesome/free-solid-svg-icons'
import './Room.css'
const socket = io('http://localhost:5000')

function Room() { 

    const [room, setRoom] = useState([])
    const [roomId, setRoomId] = useState("")
    const [buttonPopup, setButtonPopup] = useState(true);
    const { roomID } = useParams();
    const [link, setLink] = useState('');

    useEffect(() => {
        async function getRoomInfo(){
            try{
                let res = await fetch (`http://localhost:5000/posts/${roomID}`,{
                    method: 'GET'
                })
                let data = await res.json();
                const newRoom = data
                setRoom(newRoom)
                const newRoomId = data.room._id
                socket.emit('join_room', newRoomId)
                setRoomId(newRoomId)
            }catch(err){

            }
        }
        getRoomInfo();
    }, [roomID])

    const sendLink = async () => {
        const newLink = link.split("?v=")[1]
        const info = {
            link: newLink,
            room: roomID
        }
        socket.emit('send_link', info)
    };

    return (
          <div className="main-room">
            <div className="show-chat">
                <button className="show-chat-btn"  onClick={() => setButtonPopup(!buttonPopup)}><FontAwesomeIcon icon={faCommentDots} /></button>
                {buttonPopup ? <Chat socket={socket} username={localStorage.getItem('username')} room={roomId}/> : <></>}
            </div>
            <div className="youtube-video">
                <input placeholder="link..." type="text"  name="link" onChange={(e) => setLink(e.target.value)}/>
                <button onClick={sendLink}>&#9658;</button>
                <iframe width="560" height="315" src="https://www.youtube.com/embed/iLwI3-DpLuY" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
            </div>
          </div>
    )

}

export default Room;
