import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Chat from '../components/Chat';
import {io} from 'socket.io-client';

const socket = io('http://localhost:5000')

function Room() { 

    const [room, setRoom] = useState([])
    const [roomId, setRoomId] = useState("")
    const [buttonPopup, setButtonPopup] = useState(true);
    const { handle } = useParams();
    useEffect(() => {
        async function getRoomInfo(){
            try{
                let res = await fetch (`http://localhost:5000/posts/${handle}`,{
                    method: 'GET'
                })
                let data = await res.json();
                const newRoom = data
                setRoom(newRoom)
                const newRoomId = data.room._id
                socket.emit('join_room', newRoomId)
                setRoomId(newRoomId)
            }catch(err){

            }
        }
        getRoomInfo();
    }, [])

    return (
          <div>
            <button className="create-room-btn" onClick={() => setButtonPopup(true)}>Chat</button>
            <Chat socket={socket} username={localStorage.getItem('username')} room={roomId}/>
          </div>
    )

}

export default Room;
